import { configureStore } from "@reduxjs/toolkit";
import { persistStore, persistReducer } from "redux-persist";
import storage from "redux-persist/lib/storage"; // ou outro mecanismo de armazenamento, como AsyncStorage para React Native

import userReducer from "./userSlice";

const persistConfig = {
  key: "root", // a chave principal para armazenar os dados persistidos
  storage,
  // Adicione aqui qualquer configuração adicional que desejar, como whitelist, blacklist, etc.
};

const persistedReducer = persistReducer(persistConfig, userReducer);

const store = configureStore({
  reducer: {
    user: persistedReducer,
    // Adicione mais reducers conforme necessário
  },
});

const persistor = persistStore(store);

export { store, persistor };
