import React, { useState, useEffect } from 'react';
import './publish.css';
import API from '../../API'

const Publish = (props) => {

    let tipos = ['---', 'carros', 'motos', 'caminhoes'];

    const [tipoSelecionado, setTipoSelecionado] = useState(null);

    const handleSubmit = () => {
        console.log('submit');
    };

    const handleMudancaTipo = (e) => {
        setTipoSelecionado(e.target.value);
    };

    useEffect(() => {
        const subType = props.eventSubTypes.find(item => item.id === selectedSubType);
        const filteredEventsId = subType.events;
        setFilteredEvents(props.events.filter(item => filteredEventsId.includes(item.id)));
    }, [props.eventSubTypes, selectedSubType, props.events]);


    return (
        <>
            <form encType="multipart/form-data" onSubmit={handleSubmit}>

                <label htmlFor="tipoSelecionado">Tipo do veículo:</label>
                <select id="tipoSelecionado" name="tipoVeiculo" onChange={handleMudancaTipo}>
                    {tipos.map((tipo, index) => (
                        <option key={`node_id_${index}`} value={tipo}>
                            {tipo}
                        </option>
                    ))}
                </select>

                <button className="btn-form" type="submit">
                    PUBLICAR EVENTO
                </button>
            </form>
        </>
    );
};

export default Publish;
