import React, { useState } from 'react';
import './publish.css';
import API from '../../API';

const Publish = (props) => {

    let tipos = ['carros', 'motos', 'caminhoes'];
    const [tipoSelecionado, setTipoSelecionado] = useState(null);

    const [marcas, setMarcas] = useState(null);
    const [marcaSelecionada, setMarcaSelecionada] = useState(null);

    const [modelos, setModelos] = useState(null);
    const [modeloSelecionado, setModeloSelecionado] = useState(null);

    const [anos, setAnos] = useState(null);
    const [anoSelecionado, setAnoSelecioando] = useState(null);

    const [valor, setValor] = useState(null);

    const handleSubmit = () => {
        console.log('submit');
    };

    const handleMudancaTipo = async (e) => {
        const tipo = e.target.value;
        if (tipo) {
            setTipoSelecionado(tipo);
            const marcas = await API.getMarcas(tipo);
            setMarcas(marcas)
        } else {
            setTipoSelecionado(null);
            setMarcas(null);
        }
        setMarcaSelecionada(null);
        setModelos(null);
        setModeloSelecionado(null)
        setAnos(null);
        setAnoSelecioando(null);
    };

    const handleMudancaMarca = async (e) => {
        const marca = e.target.value;
        if (marca) {
            setMarcaSelecionada(marca);
            const modelos = await API.getModelos(tipoSelecionado, marca);
            setModelos(modelos.modelos);
        } else {
            setMarcaSelecionada(null);
            setModelos(null);
        }
        setModeloSelecionado(null);
        setAnos(null);
        setAnoSelecioando(null);
    };

    const handleMudancaModelo = async (e) => {
        const modelo = e.target.value;
        if (modelo) {
            setModeloSelecionado(modelo);
            const anos = await API.getAnos(tipoSelecionado, marcaSelecionada, modelo);
            setAnos(anos);
            setAnoSelecioando(null);
        } else {
            setAnos(null);
            setModeloSelecionado(null);
        }
    };

    const handleMudancaAno = async (e) => {
        const ano = e.target.value;
        if (ano) {
            setAnoSelecioando(ano);
            const v = await API.getValor(tipoSelecionado, marcaSelecionada, modeloSelecionado, ano);
            setValor(v);
        } else {
            setAnoSelecioando(null);
            setValor(v);
        }
    };

    return (
        <>
            <form encType="multipart/form-data" onSubmit={handleSubmit}>

                <label htmlFor="tipoSelecionado">Tipo do veículo:</label>
                <select id="tipoSelecionado" name="tipoVeiculo" onChange={handleMudancaTipo}>
                    <option value={''}>
                        {'---'}
                    </option>
                    {tipos.map((tipo, index) => (
                        <option key={`node_id_${index}`} value={tipo}>
                            {tipo}
                        </option>
                    ))}
                </select>

                <div>
                    <label htmlFor="marcaSelecionada">Marca do veículo:</label>
                    <select id="marcaSelecionada" name="marcaVeiculo" onChange={handleMudancaMarca}>
                        <option value={''}>
                            {'---'}
                        </option>
                        {marcas ? (
                            marcas.map((marca) => (
                                <option key={`node_id_${marca.codigo}`} value={marca.codigo}>
                                    {marca.nome}
                                </option>
                            ))
                        ) : null}
                    </select>
                </div>


                <div>
                    <label htmlFor="modeloSelecionado">Modelo do veículo:</label>
                    <select id="modeloSelecionado" name="modeloVeiculo" onChange={handleMudancaModelo}>
                        <option value={''}>
                            {'---'}
                        </option>
                        {modelos ? (
                            modelos.map((modelo) => (
                                <option key={`node_id_${modelo.codigo}`} value={modelo.codigo}>
                                    {modelo.nome}
                                </option>
                            ))
                        ) : null}
                    </select>
                </div>

                <div>
                    <label htmlFor="anoSelecionado">Ano do veículo:</label>
                    <select id="anoSelecionado" name="anoVeiculo" onChange={handleMudancaAno}>
                        <option value={''}>
                            {'---'}
                        </option>
                        {anos ? (
                            anos.map((ano) => (
                                <option key={`node_id_${ano.codigo}`} value={ano.codigo}>
                                    {ano.nome}
                                </option>
                            ))
                        ) : null}
                    </select>
                </div>

                {valor ? (
                    <p>{valor}</p>
                ) :null}


                {anoSelecionado ? (
                        <button className="btn-form" type="submit">
                            PUBLICAR CARRO
                        </button>
                ) : null}

            </form>
        </>
    );
};

export default Publish;
